import React, { useEffect, useState } from "react";
import DragIndicatorIcon from '@material-ui/icons/DragIndicator';
import Checkbox from '@material-ui/core/Checkbox';

import firebase from 'firebase'

import './styles.css';

function Todo() {

  const [item, setItem] = useState('');

  function handleChange(e) {
    firebase.database().ref('/').set(item)
  }

  useEffect(handleChange, [item])

  function handleDelete(ed) {
    firebase.database().ref(ed).remove(item)
  }

  function editListItem(value,id) {

  }

  function toggleChecked(id) {
    console.log(id,lista);
    lista.map(number => {
      if (number.id === id) {
        return ({...number, isChecked:!number.isChecked})
      }

      return number;
    })
  }

  useEffect(handleDelete, [])

  const [key, setKey] = useState(0);
  const [lista, setLista] = useState([{ id:key, value: '', isChecked: true}]);

  function addLista() {
    setKey(key+1);
    setLista([
      ...lista,
      { id:key, value: 'Fazer macarronada', isChecked: true}
    ])
  }

  return (
    <div className="Todo">
      <div className="Header">
        <h1>Sua lista de compras</h1>
      </div>
      <div className="cesta" key='initial'>
            <DragIndicatorIcon />
            <Checkbox style={{ fontSize: 24 }} />
            <input type="text"
              placeholder="Digite um novo item aqui..."
              value={item}
              onChange={e => setItem(e.target.value)}
            />
            <button onClick={ed => setItem(ed.target.value)}>X</button>
          </div>
      {lista.map(number => {
        return (
          <div className="cesta" key={number.id}>
            <DragIndicatorIcon />
            <Checkbox checked={number.isChecked} onChange={() => toggleChecked(number.id)}/>
            <input type="text"
              placeholder="Digite o nome do novo item..."
              value={number.value}
              onChange={e => editListItem(e.target.value,number.id)}
            />
            <button onClick={ed => setItem(ed.target.value)}>X</button>
          </div>
        );
      })}
      <div className="Add">
        <button onClick={addLista}>+</button>
        Adicione um novo item...
      </div>
    </div>
  );
}
export default Todo;
