import React, { useEffect, useState } from "react";
import DragIndicatorIcon from '@material-ui/icons/DragIndicator';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import firebase from 'firebase'
import PhotoCamera from '@material-ui/icons/PhotoCamera';


import './styles.css';

function Todo() {

  const [item, setItem] = useState('');

  function handleChange(e) {
    firebase.database().ref(e).set(item)
  }

  useEffect(handleChange, [item])

  function handleDelete(ed) {
    firebase.database().ref(ed).remove(item)
  }

  useEffect(handleDelete, [])


  return (
    <div className="Todo">
      <div className="Header">
        <h1>Sua lista de compras</h1>
      </div>
      <div className="cesta">
        <DragIndicatorIcon size='29px' />
        <input type='checkbox'></input>
        <input type="text"
          placeholder="Digite um novo item aqui..."
          value={item}
          onChange={e => setItem(e.target.value)}
        />
        <IconButton>
          <CloseIcon size='29px' onClick={ed => setItem(ed.target.value)} />
        </IconButton>

        <input accept="image/*" id="icon-button-file" type="file" />
        <label htmlFor="icon-button-file">
          <IconButton color="primary" aria-label="upload picture" component="span">
            <PhotoCamera />
          </IconButton>
        </label>
      </div>
    </div>
  );
}
export default Todo;
