import React, { useRef } from 'react';
import firebase from 'firebase';


function TesteFirebase() {
    const [item, setItem] = useRef('');

    function itemSubmit() {
        const newItem = this.refs.itemText.value;
        this.usersRef.push(newItem);
        this.refs.itemText.value = '';
    }


    return (
        <div>
            <h1>Contador:</h1>
            <input type="text" 
            ref="itemText" 
            placeholder="Digite um novo item aqui..." 
            value={item}
            onChange={() => setItem(this.target.value)}
             />

            <button onClick={itemSubmit}>Incrementar</button>
        </div>
    );
}

export default TesteFirebase;
