import React, { useState, useRef } from 'react';


function TesteFirebase() {
    const [item, setItem] = useRef('');

    function itemSubmit(el) {
        el.preventDefault()
        setItem();
        console.log(item)
    }


    return (
        <div>
            <h1>Contador:</h1>
            <input type="text" 
            ref="itemText" 
            placeholder="Digite um novo item aqui..." 
            value={item}
            onChange={el => setItem(e.target.value)}
             />

            <button onClick={itemSubmit}>Incrementar</button>
        </div>
    );
}

export default TesteFirebase;
