import React, { useEffect, useState } from "react";
import DragIndicatorIcon from '@material-ui/icons/DragIndicator';
import Checkbox from '@material-ui/core/Checkbox';

import firebase from 'firebase'

import './styles.css';

function Todo() {

  const [item, setItem] = useState('');

  function handleChange(list) {
    firebase.database().ref('/').set(list);
  }

  function handleDelete(ed) {
    firebase.database().ref(ed).remove(item)
  }

  function editListItem(value, id) {
    const newLista = lista.map(number => {
      if (number.id === id) {
        return ({...number, value})
      }

      return number;
    });
    setLista(newLista);
    handleChange(newLista);
  }

  function toggleChecked(id) {
    const newLista = lista.map(number => {
      if (number.id === id) {
        return ({...number, isChecked:!number.isChecked})
      }

      return number;
    });
    setLista(newLista);
    handleChange(newLista);
  }

  useEffect(handleDelete, [])

  const [lista, setLista] = useState([]);

  function addLista() {
    setLista([
      ...lista,
      { id: (Math.floor(Math.random() * 100)), value: '', isChecked: false }
    ])
  }

  return (
    <div className="Todo">
      <div className="Header">
        <h1>Sua lista de compras</h1>
      </div>
      {lista.map(number => {
        return (
          <div className="cesta" key={number.id}>
            <DragIndicatorIcon />
            <Checkbox checked={number.isChecked} onChange={() => toggleChecked(number.id)}/>
            <input type="text"
              placeholder="Digite o nome do novo item..."
              value={number.value}
              onChange={e => editListItem(e.target.value,number.id)}
            />
            <button onClick={ed => setItem(ed.target.value)}>X</button>
          </div>
        );
      })}
      <div className="Add">
        <button onClick={addLista}>+</button>
        Adicione um novo item...
      </div>
    </div>
  );
}
export default Todo;
